package de.nordakademie.iaa.library.persistent.repository;

import de.nordakademie.iaa.library.persistent.entities.Author;
import org.springframework.stereotype.Repository;

import java.util.List;
import java.util.UUID;

/**
 * This repository is for managing the database access to the author table.
 * This is only an interface. The repository will be generated by spring data.
 */
@Repository
public interface AuthorRepository extends CustomBaseRepository<Author, UUID> {


    /**
     * find all ordered by surname
     */
    List<Author> findAllByOrderBySurname();
}
